  `art_id` int(11) NOT NULL AUTO_INCREMENT,
  `art_usr_id` int(5) NOT NULL,
  `art_cat_id` int(11) NOT NULL,
  `art_title` varchar(255) NOT NULL,
  `art_metadesc` varchar(255) NOT NULL,
  `art_metakey` varchar(255) NOT NULL,
